    @Test
    public void receive() {
        AtomicInteger in = new AtomicInteger();
        DatagramPacket dp = new DatagramPacket(new byte[200 * 1024], 200 * 1024);
        DatagramSocket ds;
        try {
            ds = new DatagramSocket(514);
//            ds.setSoTimeout(1000);
            ds.setReceiveBufferSize(10);
            while (true) {
                ds.receive(dp);
                System.out.println(in.incrementAndGet());
                System.out.println(ds.getReceiveBufferSize()+":"+dp.getLength()+"接收到：" + new String(dp.getData(),0,dp.getLength()).trim());
                System.out.println(dp.getAddress()+":"+dp.getPort());
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
